import React, { Component } from 'react';

class HandleError extends Component {
    constructor(err)
    {
        super(err);
        //console.log(err);
    }
    render() { 
        return (
            <div role="alert">
              <p>Something went wrong:</p>
              <pre style={{color: 'red'}}>{this.err.message}</pre>
            </div>
        )
    }
}

export default HandleError;
